PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS players (
      id TEXT PRIMARY KEY,
      name TEXT UNIQUE NOT NULL,
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP
    );
CREATE TABLE IF NOT EXISTS games (
      id TEXT PRIMARY KEY,
      location TEXT,
      start_time DATETIME DEFAULT CURRENT_TIMESTAMP,
      end_time DATETIME,
      status TEXT DEFAULT 'active',
      winning_team TEXT
    , last_failed_transcript TEXT, target_score INTEGER, scoring_mode TEXT DEFAULT '1s2s', live_transcript TEXT);
CREATE TABLE IF NOT EXISTS rosters (
      game_id TEXT NOT NULL,
      player_id TEXT NOT NULL,
      team TEXT NOT NULL,
      PRIMARY KEY (game_id, player_id),
      FOREIGN KEY (game_id) REFERENCES games(id),
      FOREIGN KEY (player_id) REFERENCES players(id)
    );
CREATE TABLE IF NOT EXISTS game_events (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      game_id TEXT NOT NULL,
      player_id TEXT NOT NULL,
      event_type TEXT NOT NULL,
      point_value INTEGER NOT NULL,
      corrected_event_id INTEGER,
      raw_transcript TEXT,
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      FOREIGN KEY (game_id) REFERENCES games(id),
      FOREIGN KEY (player_id) REFERENCES players(id)
    );
CREATE TABLE IF NOT EXISTS game_transcripts (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      game_id TEXT NOT NULL,
      raw_text TEXT NOT NULL,
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP, acted_on TEXT,
      FOREIGN KEY (game_id) REFERENCES games(id)
    );
DELETE FROM sqlite_sequence;
CREATE INDEX idx_events_player ON game_events(player_id, event_type);
CREATE INDEX idx_events_game ON game_events(game_id, created_at);
COMMIT;
